import chardet


# 读取文件，检测编码格式，并转为python所需的编码格式
def read_file(file_path):
    with open(file_path) as fp:
        str_for_dect = fp.read(100)
        encoding = chardet.detect(str_for_dect)['encoding']
        fp.seek(0)
        content = fp.read().decode(encoding, 'ignore')
    return content

